mapper.select:


  select * from (
      select /*+INDEX_DESC(movie SYS_C008625) */ 
          rownum as rn, movieId, movieTitle, cate1, movieDate, movieCheck, poster
      from movie 
      where



        movieTitle like '%'||#{keyword}||'%' and



     rownum <= #{pageNum}*#{amount}
    )
where rn > (#{pageNum} - 1) * #{amount}
